import React from "react";
import Card from "./emoji_card";
import emojis from "../emoji_dictionary";
import Social from "./social";


function createEmoji(emj){
        return(    <Card 
            key={emj.id}
            icon={emj.emoji} 
            name={emj.name} 
            desc={emj.category}  
            code={emj.html}
            />);
}


function app(){

    return (
    <div>
    <h1 className="heading">emojipedia</h1>
    
    <div className="emoji-boxes">
        {emojis.map(createEmoji)}
    </div>
    


    <div className="social-bar">
    <li className="social">
        <a rel="noopener" href="https://github.com/subham-04"  id="github" title="Github" className="social-link">
            <i className="uil uil-github-alt"></i>
        </a>
    </li>

    <li className="social">
        <a rel="noopener" href="https://subhambhadra.netlify.app"  id="portfolio" title="Porfolio" className="social-link">
            <i className="uil uil-layer-group"></i>
        </a>
    </li>

    <li className="social">
        <a rel="noopener" href="https://www.linkedin.com/in/subham-bhadra-54b8921b2"  id="linkedin" title="Linkedin" className="social-link">
            <i className="uil uil-linkedin-alt"></i>
        </a>
    </li>

    <li className="social">
        <a rel="noopener" href="https://www.instagram.com/subham0422"  id="instagram" title="Instagram" className="social-link">
            <i className="uil uil-instagram"></i>
        </a>
    </li>
    
</div>
    

    </div>
    );
}

export default app;
