import React from "react";
import Card from "./emoji_card";
import emojis from "../emoji_dictionary";


function createEmoji(emj){
            <Card 
            icon={e.emoji} 
            name={e.name} 
            desc={e.category}  
            code={e.html}
            />
}


function app(){
    return (
    <div>
    <h1>emojipedia</h1>
    <Card icon={emojis[0].emoji} name={emojis[0].name} desc={emojis[0].category}  code={emojis[0].html}/>
    {emojis.map(createEmoji)}


    </div>
    );
}

export default app;
